mapper.select:
select a.*,b.trade_type,c.enterprise_area , d.address_name,e.enterprise_type from Transaction_policy a left join transaction_enterprise_trade b on a.enterprise_trade_id = b.trade_id left join transaction_enterprise_area c on a.enterprise_area_id = c.enterprise_area_id left join transaction_enterprise_address d on a.enterprise_address_id = d.address_id left join transaction_enterprise_type e on a.enterprise_type_id = e.enterprise_type_id where b.status = 1 and c.status = 1
and a.enterprise_address_id = #{enterpriseAddressId}
and a.enterprise_type_id = #{enterpriseTypeId}
limit #{offset},#{limit}

select count(*) from Transaction_policy a left join transaction_enterprise_trade b on a.enterprise_trade_id = b.trade_id left join transaction_enterprise_area c on a.enterprise_area_id = c.enterprise_area_id left join transaction_enterprise_address d on a.enterprise_address_id = d.address_id left join transaction_enterprise_type e on a.enterprise_type_id = e.enterprise_type_id where b.status = 1 and c.status = 1
and a.enterprise_address_id = #{enterpriseAddressId}
and a.enterprise_type_id = #{enterpriseTypeId}

select a.*,b.trade_type,c.enterprise_area , d.address_name,e.enterprise_type from Transaction_policy a left join transaction_enterprise_trade b on a.enterprise_trade_id = b.trade_id left join transaction_enterprise_area c on a.enterprise_area_id = c.enterprise_area_id left join transaction_enterprise_address d on a.enterprise_address_id = d.address_id left join transaction_enterprise_type e on a.enterprise_type_id = e.enterprise_type_id where b.status = 1 and c.status = 1
and a.enterprise_address_id = #{enterpriseAddressId}
and a.enterprise_type_id = #{enterpriseTypeId}
and a.enterprise_trade_id = #{enterpriseTradeId}
and a.policy_name like concat(concat('%', #{policyName, jdbcType=VARCHAR}),'%')
limit #{offset},#{limit}

