mapper.select:
select act.activity_id as activityId,act.activity_img as activityImg,act.activity_name as activityName,act.activity_signup_start as activitySignupStart,act.activity_signup_end as activitySignupEnd,IFNULL(b.sum,0) as sum,act.activity_person as activityPerson from transaction_activity act left JOIN (select activity_id as bid,count(*) as sum from transaction_signup GROUP BY activity_id) b ON act.activity_id=b.bid where now() < activity_end
and activity_name like concat(concat('%', #{activityName, jdbcType=VARCHAR}),'%')
limit #{offset}, #{limit}

select act.activity_id as activityId,act.activity_img as activityImg,act.activity_name as activityName,act.activity_signup_start as activitySignupStart,act.activity_signup_end as activitySignupEnd,IFNULL(b.sum,0) as sum,act.activity_person as activityPerson from transaction_activity act left JOIN (select activity_id as bid,count(*) as sum from transaction_signup GROUP BY activity_id) b ON act.activity_id=b.bid where now() > activity_end
and activity_name like concat(concat('%', #{activityName, jdbcType=VARCHAR}),'%')
limit #{offset}, #{limit}

select act.activity_id as activityId,act.activity_img as activityImg,act.activity_name as activityName,act.activity_start as activityStart,act.activity_end as activityEnd,IFNULL(b.sum,0) as sum,act.activity_person as activityPerson from transaction_activity act left JOIN (select activity_id as bid,count(*) as sum from transaction_signup where signup_status=1 GROUP BY activity_id) b ON act.activity_id=b.bid where now() > activity_end
and activity_name like concat(concat('%', #{activityName, jdbcType=VARCHAR}),'%')
limit #{offset}, #{limit}
