mapper.select:
select enterprise.member_id, member.member_name, member.member_tel, member.member_card, member.member_integral, member.member_rank, member.member_online_times, member.member_date, enterprise.enterprise_name, enterprise.enterprise_card_type, enterprise.enterprise_card_id, enterprise.enterprise_taxpayer,
enterprise.enterprise_validity_date_end, enterprise.enterprise_certification_unit, enterprise.enterprise_reg_address, enterprise.enterprise_reg_date, enterprise.enterprise_reg_money, enterprise.enterprise_legal_person_name, enterprise.enterprise_legal_person_card, trade.trade_type, type.enterprise_type, enterprise_status.enterprise_status, area.enterprise_area, address.address_name from transaction_enterprise enterprise left join transaction_member member on member.member_id = enterprise.member_id left join transaction_enterprise_area area on enterprise.enterprise_area_id = area.enterprise_area_id left join transaction_enterprise_type type on enterprise.enterprise_type_id = type.enterprise_type_id left join transaction_enterprise_trade trade on enterprise.enterprise_trade_id= trade.trade_id left join transaction_enterprise_status enterprise_status on enterprise.enterprise_status_id=enterprise_status.enterprise_status_id left join transaction_enterprise_address address on enterprise.enterprise_address_id=address.address_id
where enterprise.member_id=#{memberId};
