mapper.select:
select reghost.* ,service.*,enterprise.*,member.* from transaction_reghost reghost join transaction_service service on reghost.service_id=service.service_id join transaction_member member on member.member_id=reghost.member_id join transaction_enterprise enterprise on enterprise.member_id = member.member_id
enterprise_name like concat(concat('%', #{enterpriseName, jdbcType=VARCHAR}),'%')
limit #{offset}, #{limit}
