select financing.* from transaction_financing financing join transaction_enterprise_trade trade on trade.trade_id=financing.trade_id join transaction_enterprise_address address on address.address_id=financing.address_id where now() < financing_end
and financing_name like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%') or address_name like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%') or trade_type like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%')
and trade.trade_id = #{tradeId}
and address.address_id = #{addressId}
limit #{offset}, #{limit}

select count(*) from transaction_financing financing join transaction_enterprise_trade trade on trade.trade_id=financing.trade_id join transaction_enterprise_address address on address.address_id=financing.address_id where now() < financing_end
and financing_name like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%') or address_name like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%') or trade_type like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%')
and trade.trade_id = #{tradeId}
and address.address_id = #{addressId}

select * from transaction_financing where now() > financing_end
and financing_name like concat(concat('%', #{financingName, jdbcType=VARCHAR}),'%')
limit #{offset}, #{limit}

