mapper.insert:
insert into 
transaction_rich_text(title, content, create_time, update_time, title_status) 
values(#{title}, #{content}, #{createTime}, #{updateTime},#{titleStatus});

mapper.update:
update transaction_rich_text
set title=#{title}, content=#{content},create_time=#{createTime},update_time=#{updateTime},title_status=#{titleStatus}
where title_id=#{titleId};
